import React, { useState } from 'react';
import Papa from 'papaparse'; // For parsing CSV files

function App() {
  const [data, setData] = useState([]);
  const [sortDirection, setSortDirection] = useState('asc'); // State to track sorting direction

  // Function to handle file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    
    // Using PapaParse to parse the CSV file
    Papa.parse(file, {
      complete: (result) => {
        setData(result.data.slice(1)); // Skip header row
      }
    });
  };

  // Function to open Leetcode question link in a new tab
  const openLeetCodeLink = (link) => {
    window.open(link, '_blank');
  };

  // Function to sort data based on Difficulty column
  const sortData = () => {
    const sortedData = [...data];
    if (sortDirection === 'asc') {
      sortedData.sort((a, b) => parseFloat(a[3]) - parseFloat(b[3]));
      setSortDirection('desc');
    } else {
      sortedData.sort((a, b) => parseFloat(b[3]) - parseFloat(a[3]));
      setSortDirection('asc');
    }
    setData(sortedData);
  };

  return (
    <div>
      <h1>CSV File Reader</h1>
      <input type="file" accept=".csv" onChange={handleFileUpload} />
      <button onClick={sortData}>Sort by Difficulty</button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Acceptance</th>
            <th>Difficulty</th>
            <th>Frequency</th>
            <th>Leetcode Question Link</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row[0]}</td>
              <td>{row[1]}</td>
              <td>{row[2]}</td>
              <td>{row[3]}</td>
              <td>{row[4]}</td>
              <td>
                <button onClick={() => openLeetCodeLink(row[5])}>Open</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
